import React, { PureComponent } from 'react';
import { Space, Input, Typography, Card, Image, Tooltip, Modal, message, Divider } from 'antd';
import styles from '../System/system.less';
import global from '@/global.less';
import Footer from '@/components/Footer';
import { LoginOutlined, ArrowRightOutlined } from '@ant-design/icons';
import { numberDateFormat } from '@/utils/utils';
import { history } from 'umi';

import 'antd/es/modal/style';
import 'antd/es/slider/style';

const { Search } = Input;
const { Text, Paragraph, Title } = Typography;
const { Meta } = Card;
const { confirm } = Modal;

class Index extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
      searchValue: '',
      rows: 3,
      oilFieldArray: [
        { name: '冀东油田', projects: [] },
        { name: '大港油田', projects: [] },
        { name: '辽河油田', projects: [] },
      ],
    };
  }

  componentDidMount() {
    this.getAllProjects();
  }

  // 获取所有工程
  getAllProjects = async () => {
    const { searchValue } = this.state;
    let res = await queryArtislandProjectByCondition(searchValue, 'create_time', 'desc');
    if (res.code === '0000') {
      let oilFieldArray = [
        { name: '冀东油田', projects: [] },
        { name: '大港油田', projects: [] },
        { name: '辽河油田', projects: [] },
      ];
      res.result.sort((a, b) => b.createTime - a.createTime);
      for (let item of res.result) {
        let obj = Object.create(null);
        obj.id = item.id;
        obj.artislandName = item.artislandName;
        obj.createTime = item.createTime;
        obj.buildTime = item.buildTime;
        obj.areaConvered = item.areaConvered;
        obj.coastlineLength = item.coastlineLength;
        obj.dikeLength = item.dikeLength;
        obj.safetyLevel = item.safetyLevel;
        obj.artislandSummary = item.artislandSummary;
        obj.artislandImage = item.artislandImage;
        obj.oilfield = item.oilfield;
        if (obj.oilfield !== null) {
          for (let item of oilFieldArray) {
            if (item.name === obj.oilfield) {
              item.projects.push(obj);
            }
          }
        }
      }
      this.setState({
        oilFieldArray,
      });
    } else {
      message.error(res.message);
    }
  };

  // 工程搜索
  onSearch = (value) => {
    this.setState(
      {
        searchValue: value,
      },
      () => {
        this.getAllProjects();
      },
    );
  };

  // 进入人工岛工程
  intoProject = (item) => {
    localStorage.setItem('projectId', item.id);
    history.push(`/home`);
  };

  render() {
    const { isVisible, type, oilFieldArray, rows } = this.state;
    const content = oilFieldArray.map((oil, index) => {
      return (
        <div key={index}>
          <Title level={3} className={styles.oilTitle}>
            {oil.name}
          </Title>
          <Divider />
          <div style={{ display: 'flex', flexWrap: 'wrap' }}>
            {oil.projects.map((item, index) => {
              return (
                <Card
                  style={{ width: 450, margin: 10 }}
                  key={index}
                  actions={[
                    <Text
                      className={styles.operateButton}
                      onClick={this.intoProject.bind(this, item)}
                    >
                      <ArrowRightOutlined key="detail" style={{ marginRight: 5 }} />
                      进入人工岛
                    </Text>,
                  ]}
                >
                  <Meta
                    avatar={
                      <>
                        <div>
                          <Image
                            width={100}
                            height={100}
                            style={{ objectFit: 'cover' }}
                            src={
                              item.artislandImage !== ''
                                ? downloadFileByPath(item.artislandImage)
                                : 'data:image/png;base64,'
                            }
                            fallback="data:image/png;base64,"
                          />
                        </div>
                        <div>
                          {item.safetyLevel === '待评估' && (
                            <div shape="round" size="large" className={styles.safetyLevelNoButton}>
                              {item.safetyLevel}
                            </div>
                          )}
                          {item.safetyLevel === '一级' && (
                            <div shape="round" size="large" className={styles.safetyLevelOneButton}>
                              {item.safetyLevel}
                            </div>
                          )}
                          {item.safetyLevel === '二级' && (
                            <div shape="round" size="large" className={styles.safetyLevelTwoButton}>
                              {item.safetyLevel}
                            </div>
                          )}
                          {item.safetyLevel === '三级' && (
                            <div
                              shape="round"
                              size="large"
                              className={styles.safetyLevelThreeButton}
                            >
                              {item.safetyLevel}
                            </div>
                          )}
                          {item.safetyLevel === '四级' && (
                            <div
                              shape="round"
                              size="large"
                              className={styles.safetyLevelFourButton}
                            >
                              {item.safetyLevel}
                            </div>
                          )}
                          {item.safetyLevel === '五级' && (
                            <div
                              shape="round"
                              size="large"
                              className={styles.safetyLevelFiveButton}
                            >
                              {item.safetyLevel}
                            </div>
                          )}
                        </div>
                      </>
                    }
                    title={
                      <Title
                        level={4}
                        style={{
                          whiteSpace: 'normal',
                        }}
                      >
                        {item.artislandName}
                      </Title>
                    }
                    description={
                      <Space direction="vertical">
                        <Text>
                          建设时间：
                          {item.buildTime === 0
                            ? '无'
                            : numberDateFormat(item.buildTime, 'yyyy-MM-dd')}
                        </Text>
                        <Text>
                          占地面积：{item.areaConvered === '' ? '无' : `${item.areaConvered} ㎡`}
                        </Text>
                        <Text>
                          海岸线长度：
                          {item.coastlineLength === '' ? '无' : `${item.coastlineLength} m`}
                        </Text>
                        <Text>
                          围堤长度：{item.dikeLength === '' ? '无' : `${item.dikeLength} m`}
                        </Text>
                        <Text>
                          {item.artislandSummary === '' ? (
                            '项目概况：无'
                          ) : (
                            <Tooltip title={item.artislandSummary}>
                              <Paragraph
                                ellipsis={{
                                  rows,
                                  expandable: false,
                                }}
                              >
                                项目概况：{item.artislandSummary}
                              </Paragraph>
                            </Tooltip>
                          )}
                        </Text>
                      </Space>
                    }
                  ></Meta>
                </Card>
              );
            })}
          </div>
        </div>
      );
    });
    return (
      <div className={global.MyMain}>
        <div className={global.MyContent}>
          <div className={global.MyHeader}>
            <div className={global.MyTitle}>
              <div className={global.MyTitlePoint}>
                <img
                  src={require('@/assets/image/icon_title.png')}
                  className={global.MyTitleImg}
                ></img>
              </div>
              <Text style={{ fontSize: 16 }}>人工岛视图</Text>
            </div>
            <Space>
              <Search placeholder="请输入项目名称" onSearch={this.onSearch} enterButton />
            </Space>
          </div>
          <div className={global.MyBody}>
            <div className={styles.projects}>{content}</div>
          </div>
        </div>
        <div className={global.MyFooter}>
          <Footer />
        </div>
      </div>
    );
  }
}

export default Index;
